import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TransactionForm from './TransactionForm';
import TransactionList from './TransactionList';
import SavingsGoalForm from './SavingsGoalForm';
import SavingsGoalCard from './SavingsGoalCard';
import './Dashboard.css';

const API_URL = 'http://localhost:5000/api';

function Dashboard() {
  const [transactions, setTransactions] = useState([]);
  const [savingsGoals, setSavingsGoals] = useState([]);
  const [balance, setBalance] = useState(0);
  
  // Feature #11: Suche und Filter
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState('all');

  useEffect(() => {
    fetchTransactions();
    fetchSavingsGoals();
  }, []);

  // Feature #1: GET All Transactions
  const fetchTransactions = async () => {
    try {
      const response = await axios.get(`${API_URL}/transactions`);
      setTransactions(response.data);
      calculateBalance(response.data); // Feature #3: GET Summary (Balance)
    } catch (err) {
      console.error('Fehler beim Laden der Transaktionen:', err);
    }
  };

  // Feature #2: GET All Savings Goals
  const fetchSavingsGoals = async () => {
    try {
      const response = await axios.get(`${API_URL}/savings-goals`);
      setSavingsGoals(response.data);
    } catch (err) {
      console.error('Fehler beim Laden der Sparziele:', err);
    }
  };

  const calculateBalance = (txns) => {
    const total = txns.reduce((acc, txn) => {
      const amount = Number(txn.amount) || 0;
      return txn.type === 'income' ? acc + amount : acc - amount;
    }, 0);
    setBalance(total);
  };

  // Feature #4: POST New Transaction + Feature #10: Auto-Link Logic
  const addTransaction = async (transaction) => {
    try {
      await axios.post(`${API_URL}/transactions`, transaction);
      
      const matchingGoal = savingsGoals.find(goal => 
        transaction.text.toLowerCase().includes(goal.name.toLowerCase())
      );

      if (matchingGoal && transaction.type === 'expense') {
        const newAmount = matchingGoal.currentAmount + parseFloat(transaction.amount);
        // Feature #6: PATCH Savings Amount
        await axios.patch(`${API_URL}/savings-goals/${matchingGoal._id}`, { 
          currentAmount: newAmount 
        });
      }
      fetchTransactions();
      fetchSavingsGoals();
    } catch (err) {
      console.error('Fehler:', err);
    }
  };

  // Feature #8: DELETE Transaction
  const deleteTransaction = async (id) => {
    try {
      await axios.delete(`${API_URL}/transactions/${id}`);
      fetchTransactions();
    } catch (err) {
      console.error('Fehler beim L√∂schen:', err);
    }
  };

  // Feature #5: POST New Savings Goal
  const addSavingsGoal = async (goal) => {
    try {
      await axios.post(`${API_URL}/savings-goals`, goal);
      fetchSavingsGoals();
    } catch (err) {
      console.error('Fehler beim Erstellen des Sparziels:', err);
    }
  };

  // Feature #7: PATCH Update Savings Goal
  const updateSavingsGoal = async (id, amount) => {
    try {
      await axios.patch(`${API_URL}/savings-goals/${id}`, { currentAmount: amount });
      fetchSavingsGoals();
    } catch (err) {
      console.error('Fehler beim Aktualisieren:', err);
    }
  };

  // Feature #9: DELETE Savings Goal
  const deleteSavingsGoal = async (id) => {
    try {
      await axios.delete(`${API_URL}/savings-goals/${id}`);
      fetchSavingsGoals();
    } catch (err) {
      console.error('Fehler beim L√∂schen:', err);
    }
  };

  // Logik f√ºr Feature #11: Echtzeit-Filterung
  const filteredTransactions = transactions.filter(txn => {
    const matchesSearch = txn.text.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = filterType === 'all' || txn.type === filterType;
    return matchesSearch && matchesType;
  });

  return (
    <div className="dashboard">
      <header>
        <h1>üí∞ Budget-Tracker</h1>
        <div className="balance">
          <h2>Aktueller Stand: CHF {balance.toFixed(2)}</h2>
        </div>
      </header>

      <div className="main-content">
        <section className="transactions-section">
          <h2>Transaktionen</h2>
          
          <div className="filter-section">
            <h3 className="section-label">üîç Liste durchsuchen & filtern</h3>
            <div className="filter-bar">
              <input 
                type="text" 
                placeholder="Name suchen..." 
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
              <select value={filterType} onChange={(e) => setFilterType(e.target.value)} className="filter-select">
                <option value="all">Alle Typen</option>
                <option value="income">Nur Einnahmen</option>
                <option value="expense">Nur Ausgaben</option>
              </select>
            </div>
          </div>

          <hr className="divider" />

          <div className="add-section">
            <h3 className="section-label">‚ûï Neue Buchung erfassen</h3>
            <TransactionForm onAdd={addTransaction} savingsGoals={savingsGoals} /> 
          </div>
          
          <hr className="divider" />

          <TransactionList transactions={filteredTransactions} onDelete={deleteTransaction} />
        </section>

        <section className="savings-section">
          <h2>üéØ Sparziele f√ºr Grossinvestitionen</h2>
          <SavingsGoalForm onAdd={addSavingsGoal} />
          <div className="savings-grid">
            {/* Feature #12: Alphabetische Sortierung */}
            {[...savingsGoals]
              .sort((a, b) => a.name.localeCompare(b.name))
              .map((goal) => (
                <SavingsGoalCard
                  key={goal._id}
                  goal={goal}
                  onUpdate={updateSavingsGoal}
                  onDelete={deleteSavingsGoal}
                />
              ))}
          </div>
        </section>
      </div>
    </div>
  );
}

export default Dashboard;
