import React, { useState } from 'react';

function TransactionForm({ onAdd }) {
  const [text, setText] = useState('');
  const [amount, setAmount] = useState('');
  const [type, setType] = useState('expense'); // Standardmäßig 'Ausgabe'

  const handleSubmit = (e) => {
    e.preventDefault();

    // WICHTIG: Hier bereiten wir die Daten exakt für das Backend vor
    const transaction = {
      text: text, // Backend erwartet 'text'
      amount: parseFloat(amount), // Muss eine Zahl sein
      type: type // Muss 'income' oder 'expense' sein
    };

    onAdd(transaction);
    
    // Formular leeren
    setText('');
    setAmount('');
  };

  return (
    <form className="transaction-form" onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Was hast du gemacht? (z.B. Lohn)"
        value={text}
        onChange={(e) => setText(e.target.value)}
        required
      />
      <input
        type="number"
        placeholder="Betrag in CHF"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
        required
      />
      <select value={type} onChange={(e) => setType(e.target.value)}>
        <option value="expense">Ausgabe (-)</option>
        <option value="income">Einnahme (+)</option>
      </select>
      <button type="submit">Hinzufügen</button>
    </form>
  );
}

export default TransactionForm;
