import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TransactionForm from './TransactionForm';
import TransactionList from './TransactionList';
import SavingsGoalForm from './SavingsGoalForm';
import SavingsGoalCard from './SavingsGoalCard';
import './Dashboard.css';

const API_URL = 'http://localhost:5000/api';

function Dashboard() {
  const [transactions, setTransactions] = useState([]);
  const [savingsGoals, setSavingsGoals] = useState([]);

  const [incomeTotal, setIncomeTotal] = useState(0);
  const [expenseTotal, setExpenseTotal] = useState(0);
  const [balance, setBalance] = useState(0);

  // Suche (nur fÃ¼r letzte Transaktionen)
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState('all');
  const [searchExecuted, setSearchExecuted] = useState(false);

  useEffect(() => {
    fetchTransactions();
    fetchSavingsGoals();
  }, []);

  const getTxnText = (txn) =>
    txn.text || txn.description || txn.name || '';

  const fetchTransactions = async () => {
    const res = await axios.get(`${API_URL}/transactions`);
    setTransactions(res.data);
    calculateSummary(res.data);
  };

  const fetchSavingsGoals = async () => {
    const res = await axios.get(`${API_URL}/savings-goals`);
    setSavingsGoals(res.data);
  };

  const calculateSummary = (txns) => {
    let income = 0;
    let expense = 0;

    txns.forEach(txn => {
      const amount = Number(txn.amount) || 0;
      txn.type === 'income'
        ? income += amount
        : expense += amount;
    });

    setIncomeTotal(income);
    setExpenseTotal(expense);
    setBalance(income - expense);
  };

  const addTransaction = async (transaction) => {
    await axios.post(`${API_URL}/transactions`, transaction);
    fetchTransactions();
  };

  const deleteTransaction = async (id) => {
    await axios.delete(`${API_URL}/transactions/${id}`);
    fetchTransactions();
  };

  const addSavingsGoal = async (goal) => {
    await axios.post(`${API_URL}/savings-goals`, goal);
    fetchSavingsGoals();
  };

  const updateSavingsGoal = async (id, amount) => {
    await axios.patch(`${API_URL}/savings-goals/${id}`, { currentAmount: amount });
    fetchSavingsGoals();
  };

  const deleteSavingsGoal = async (id) => {
    await axios.delete(`${API_URL}/savings-goals/${id}`);
    fetchSavingsGoals();
  };

  const executeSearch = () => setSearchExecuted(true);

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') executeSearch();
  };

  // ðŸ”Ÿ letzte 10 Transaktionen (neueste zuerst)
  const lastTenTransactions = [...transactions]
    .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
    .slice(0, 10);

  const filteredLastTransactions = searchExecuted
    ? lastTenTransactions.filter(txn => {
        const text = getTxnText(txn).toLowerCase();
        const matchesSearch = text.includes(searchTerm.trim().toLowerCase());
        const matchesType = filterType === 'all' || txn.type === filterType;
        return matchesSearch && matchesType;
      })
    : lastTenTransactions;

  return (
    <div className="dashboard">
      {/* HEADER */}
      <header className="header">
        <h1>ðŸ’° Budget Tracker</h1>

        <div className="summary">
          <div className="summary-card income">
            <span>Einnahmen</span>
            <strong>CHF {incomeTotal.toFixed(2)}</strong>
          </div>

          <div className="summary-card expense">
            <span>Ausgaben</span>
            <strong>CHF {expenseTotal.toFixed(2)}</strong>
          </div>

          <div className="summary-card balance">
            <span>Saldo</span>
            <strong>CHF {balance.toFixed(2)}</strong>
          </div>
        </div>
      </header>

      <div className="layout">
        {/* LINKS: Erfassen + Sparziele */}
        <div className="left-column">
          <section className="card">
            <h2>âž• Neue Transaktion</h2>
            <TransactionForm onAdd={addTransaction} savingsGoals={savingsGoals} />
          </section>

          <section className="card">
            <h2>ðŸŽ¯ Sparziele</h2>
            <SavingsGoalForm onAdd={addSavingsGoal} />

            <div className="savings-grid">
              {[...savingsGoals]
                .sort((a, b) => a.name.localeCompare(b.name))
                .map(goal => (
                  <SavingsGoalCard
                    key={goal._id}
                    goal={goal}
                    onUpdate={updateSavingsGoal}
                    onDelete={deleteSavingsGoal}
                  />
                ))}
            </div>
          </section>
        </div>

        {/* RECHTS: Letzte 10 Transaktionen + Suche */}
        <section className="card">
          <h2>ðŸ•’ Letzte 10 Transaktionen</h2>

          <div className="search-box">
            <input
              type="text"
              placeholder="Transaktion suchenâ€¦"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              onKeyDown={handleKeyDown}
            />

            <select
              value={filterType}
              onChange={(e) => setFilterType(e.target.value)}
            >
              <option value="all">Alle</option>
              <option value="income">Einnahmen</option>
              <option value="expense">Ausgaben</option>
            </select>

            <button onClick={executeSearch}>Suchen</button>
          </div>

          {filteredLastTransactions.length === 0 ? (
            <p className="empty">Keine passenden Transaktionen</p>
          ) : (
            <TransactionList
              transactions={filteredLastTransactions}
              onDelete={deleteTransaction}
            />
          )}
        </section>
      </div>
    </div>
  );
}

export default Dashboard;
