import React, { useState } from 'react';

export default function SavingsGoalForm({ onAdd }) {
  const [name, setName] = useState('');
  const [targetAmount, setTargetAmount] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!name || !targetAmount) return;

    // Wir senden die Daten an die Funktion im Dashboard
    onAdd({
      name,
      targetAmount: parseFloat(targetAmount),
      currentAmount: 0
    });

    // Formular zurücksetzen
    setName('');
    setTargetAmount('');
  };

  return (
    <div className="goal-form-container" style={{ marginBottom: '20px', padding: '15px', border: '1px solid #eee' }}>
      <h3>Neues Sparziel festlegen</h3>
      <form onSubmit={handleSubmit} style={{ display: 'flex', gap: '10px' }}>
        <input
          type="text"
          placeholder="Name (z.B. Urlaub)"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Zielbetrag (CHF)"
          value={targetAmount}
          onChange={(e) => setTargetAmount(e.target.value)}
          required
        />
        <button type="submit" style={{ backgroundColor: '#27ae60', color: 'white', border: 'none', padding: '5px 15px', borderRadius: '4px' }}>
          Hinzufügen
        </button>
      </form>
    </div>
  );
}
