import React, { useState } from 'react';
import './SavingsGoalForm.css';
import TransactionForm from './TransactionForm';

function SavingsGoalForm({ onAdd }) {
  const [formData, setFormData] = useState({
    name: '',
    targetAmount: '',
    currentAmount: '',
    category: 'Haus',
    deadline: '',
    monthlyContribution: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    onAdd({
      ...formData,
      targetAmount: parseFloat(formData.targetAmount),
      currentAmount: parseFloat(formData.currentAmount) || 0,
      monthlyContribution: parseFloat(formData.monthlyContribution) || 0
    });
    setFormData({
      name: '',
      targetAmount: '',
      currentAmount: '',
      category: 'Haus',
      deadline: '',
      monthlyContribution: ''
    });
  };

  return (
    <form className="savings-goal-form" onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Name (z.B. Traumhaus Z√ºrich)"
        value={formData.name}
        onChange={(e) => setFormData({...formData, name: e.target.value})}
        required
      />

      <select 
        value={formData.category}
        onChange={(e) => setFormData({...formData, category: e.target.value})}
      >
        <option value="Haus">üè† Haus</option>
        <option value="Boot">‚õµ Boot</option>
        <option value="Auto">üöó Auto</option>
        <option value="Andere">üíé Andere</option>
      </select>

      <input
        type="number"
        step="0.01"
        placeholder="Zielbetrag (CHF)"
        value={formData.targetAmount}
        onChange={(e) => setFormData({...formData, targetAmount: e.target.value})}
        required
      />

      <input
        type="number"
        step="0.01"
        placeholder="Bereits gespart (CHF)"
        value={formData.currentAmount}
        onChange={(e) => setFormData({...formData, currentAmount: e.target.value})}
      />

      <input
        type="number"
        step="0.01"
        placeholder="Monatlicher Sparbetrag (CHF)"
        value={formData.monthlyContribution}
        onChange={(e) => setFormData({...formData, monthlyContribution: e.target.value})}
      />

      <input
        type="date"
        placeholder="Zieldatum"
        value={formData.deadline}
        onChange={(e) => setFormData({...formData, deadline: e.target.value})}
      />

      <button type="submit">Sparziel erstellen</button>
    </form>
  );
}

export default TransactionForm;
