import React from 'react';
import './TransactionList.css';

function TransactionList({ transactions, onDelete }) {
  return (
    <div className="transaction-list">
      {transactions.map(txn => (
        <div key={txn._id} className={`transaction-item ${txn.type}`}>
          <div className="transaction-info">
            <span className="category">{txn.category}</span>
            <span className="description">{txn.description}</span>
            <span className="date">{new Date(txn.date).toLocaleDateString('de-CH')}</span>
          </div>
          <div className="transaction-amount">
            <span className={txn.type}>
              {txn.type === 'income' ? '+' : '-'} CHF {txn.amount.toFixed(2)}
            </span>
            <button onClick={() => onDelete(txn._id)}>üóëÔ∏è</button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default TransactionList;
