import React, { useState, useEffect } from 'react';
import { Wallet, TrendingUp, TrendingDown, DollarSign, Search, Plus, Target, Trash2, Edit2, Check, X } from 'lucide-react';

// Mock API - In production würdest du axios verwenden
const mockAPI = {
  transactions: [
    { _id: '1', text: 'Gehalt', amount: 5000, type: 'income', createdAt: new Date().toISOString() },
    { _id: '2', text: 'Miete', amount: 1500, type: 'expense', createdAt: new Date(Date.now() - 86400000).toISOString() },
    { _id: '3', text: 'Einkaufen', amount: 250, type: 'expense', createdAt: new Date(Date.now() - 172800000).toISOString() },
  ],
  savingsGoals: [
    { _id: '1', name: 'Notfallfonds', targetAmount: 10000, currentAmount: 3500 },
    { _id: '2', name: 'Urlaub', targetAmount: 3000, currentAmount: 1200 },
  ]
};

function Dashboard() {
  const [transactions, setTransactions] = useState(mockAPI.transactions);
  const [savingsGoals, setSavingsGoals] = useState(mockAPI.savingsGoals);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState('all');
  const [showAddTransaction, setShowAddTransaction] = useState(false);
  const [showAddGoal, setShowAddGoal] = useState(false);

  // Berechne Zusammenfassung
  const incomeTotal = transactions
    .filter(t => t.type === 'income')
    .reduce((sum, t) => sum + Number(t.amount), 0);
  
  const expenseTotal = transactions
    .filter(t => t.type === 'expense')
    .reduce((sum, t) => sum + Number(t.amount), 0);
  
  const balance = incomeTotal - expenseTotal;

  // Letzte 10 Transaktionen mit Filter
  const filteredTransactions = transactions
    .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
    .slice(0, 10)
    .filter(txn => {
      const matchesSearch = txn.text.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesType = filterType === 'all' || txn.type === filterType;
      return matchesSearch && matchesType;
    });

  const addTransaction = (transaction) => {
    const newTxn = {
      _id: Date.now().toString(),
      ...transaction,
      createdAt: new Date().toISOString()
    };
    setTransactions([newTxn, ...transactions]);
    setShowAddTransaction(false);
  };

  const deleteTransaction = (id) => {
    setTransactions(transactions.filter(t => t._id !== id));
  };

  const addSavingsGoal = (goal) => {
    const newGoal = {
      _id: Date.now().toString(),
      ...goal,
      currentAmount: 0
    };
    setSavingsGoals([...savingsGoals, newGoal]);
    setShowAddGoal(false);
  };

  const updateSavingsGoal = (id, amount) => {
    setSavingsGoals(savingsGoals.map(g => 
      g._id === id ? { ...g, currentAmount: amount } : g
    ));
  };

  const deleteSavingsGoal = (id) => {
    setSavingsGoals(savingsGoals.filter(g => g._id !== id));
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 via-white to-purple-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="flex items-center gap-3 mb-6">
            <div className="p-2 bg-gradient-to-br from-indigo-500 to-purple-600 rounded-xl">
              <Wallet className="w-8 h-8 text-white" />
            </div>
            <h1 className="text-3xl font-bold bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent">
              Budget Tracker
            </h1>
          </div>

          {/* Summary Cards */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="bg-gradient-to-br from-green-50 to-emerald-50 rounded-xl p-5 border border-green-200">
              <div className="flex items-center justify-between mb-2">
                <span className="text-sm font-medium text-green-700">Einnahmen</span>
                <TrendingUp className="w-5 h-5 text-green-600" />
              </div>
              <div className="text-2xl font-bold text-green-900">
                CHF {incomeTotal.toFixed(2)}
              </div>
            </div>

            <div className="bg-gradient-to-br from-red-50 to-pink-50 rounded-xl p-5 border border-red-200">
              <div className="flex items-center justify-between mb-2">
                <span className="text-sm font-medium text-red-700">Ausgaben</span>
                <TrendingDown className="w-5 h-5 text-red-600" />
              </div>
              <div className="text-2xl font-bold text-red-900">
                CHF {expenseTotal.toFixed(2)}
              </div>
            </div>

            <div className={`bg-gradient-to-br rounded-xl p-5 border ${
              balance >= 0 
                ? 'from-blue-50 to-indigo-50 border-blue-200' 
                : 'from-orange-50 to-red-50 border-orange-200'
            }`}>
              <div className="flex items-center justify-between mb-2">
                <span className={`text-sm font-medium ${balance >= 0 ? 'text-blue-700' : 'text-orange-700'}`}>
                  Saldo
                </span>
                <DollarSign className={`w-5 h-5 ${balance >= 0 ? 'text-blue-600' : 'text-orange-600'}`} />
              </div>
              <div className={`text-2xl font-bold ${balance >= 0 ? 'text-blue-900' : 'text-orange-900'}`}>
                CHF {balance.toFixed(2)}
              </div>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Left Column - Transactions */}
          <div className="lg:col-span-2 space-y-6">
            {/* Add Transaction */}
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-lg font-semibold text-gray-900">Neue Transaktion</h2>
                <button
                  onClick={() => setShowAddTransaction(!showAddTransaction)}
                  className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
                >
                  {showAddTransaction ? <X className="w-5 h-5" /> : <Plus className="w-5 h-5" />}
                </button>
              </div>
              
              {showAddTransaction && (
                <TransactionForm onAdd={addTransaction} />
              )}
            </div>

            {/* Recent Transactions */}
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <h2 className="text-lg font-semibold text-gray-900 mb-4">Letzte Transaktionen</h2>
              
              {/* Search & Filter */}
              <div className="flex gap-3 mb-4">
                <div className="flex-1 relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
                  <input
                    type="text"
                    placeholder="Transaktion suchen..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                  />
                </div>
                <select
                  value={filterType}
                  onChange={(e) => setFilterType(e.target.value)}
                  className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
                >
                  <option value="all">Alle</option>
                  <option value="income">Einnahmen</option>
                  <option value="expense">Ausgaben</option>
                </select>
              </div>

              {/* Transaction List */}
              <div className="space-y-2">
                {filteredTransactions.length === 0 ? (
                  <p className="text-center text-gray-500 py-8">Keine Transaktionen gefunden</p>
                ) : (
                  filteredTransactions.map(txn => (
                    <div
                      key={txn._id}
                      className="flex items-center justify-between p-4 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors"
                    >
                      <div className="flex items-center gap-3">
                        <div className={`p-2 rounded-lg ${
                          txn.type === 'income' 
                            ? 'bg-green-100 text-green-600' 
                            : 'bg-red-100 text-red-600'
                        }`}>
                          {txn.type === 'income' ? <TrendingUp className="w-4 h-4" /> : <TrendingDown className="w-4 h-4" />}
                        </div>
                        <div>
                          <div className="font-medium text-gray-900">{txn.text}</div>
                          <div className="text-xs text-gray-500">
                            {new Date(txn.createdAt).toLocaleDateString('de-CH')}
                          </div>
                        </div>
                      </div>
                      <div className="flex items-center gap-3">
                        <span className={`font-semibold ${
                          txn.type === 'income' ? 'text-green-600' : 'text-red-600'
                        }`}>
                          {txn.type === 'income' ? '+' : '-'} CHF {Number(txn.amount).toFixed(2)}
                        </span>
                        <button
                          onClick={() => deleteTransaction(txn._id)}
                          className="p-1 hover:bg-red-100 rounded text-red-600 transition-colors"
                        >
                          <Trash2 className="w-4 h-4" />
                        </button>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </div>
          </div>

          {/* Right Column - Savings Goals */}
          <div className="space-y-6">
            <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
              <div className="flex items-center justify-between mb-4">
                <h2 className="text-lg font-semibold text-gray-900">Sparziele</h2>
                <button
                  onClick={() => setShowAddGoal(!showAddGoal)}
                  className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
                >
                  {showAddGoal ? <X className="w-5 h-5" /> : <Plus className="w-5 h-5" />}
                </button>
              </div>

              {showAddGoal && (
                <SavingsGoalForm onAdd={addSavingsGoal} />
              )}

              <div className="space-y-4 mt-4">
                {savingsGoals.sort((a, b) => a.name.localeCompare(b.name)).map(goal => (
                  <SavingsGoalCard
                    key={goal._id}
                    goal={goal}
                    onUpdate={updateSavingsGoal}
                    onDelete={deleteSavingsGoal}
                  />
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

function TransactionForm({ onAdd }) {
  const [formData, setFormData] = useState({
    text: '',
    amount: '',
    type: 'expense'
  });

  const handleSubmit = () => {
    if (formData.text && formData.amount) {
      onAdd({
        text: formData.text,
        amount: parseFloat(formData.amount),
        type: formData.type
      });
      setFormData({ text: '', amount: '', type: 'expense' });
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSubmit();
    }
  };

  return (
    <div className="space-y-4">
      <input
        type="text"
        placeholder="Beschreibung"
        value={formData.text}
        onChange={(e) => setFormData({ ...formData, text: e.target.value })}
        onKeyPress={handleKeyPress}
        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
      />
      <input
        type="number"
        step="0.01"
        placeholder="Betrag"
        value={formData.amount}
        onChange={(e) => setFormData({ ...formData, amount: e.target.value })}
        onKeyPress={handleKeyPress}
        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
      />
      <div className="flex gap-3">
        <button
          onClick={() => setFormData({ ...formData, type: 'income' })}
          className={`flex-1 py-2 px-4 rounded-lg font-medium transition-colors ${
            formData.type === 'income'
              ? 'bg-green-500 text-white'
              : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
          }`}
        >
          Einnahme
        </button>
        <button
          onClick={() => setFormData({ ...formData, type: 'expense' })}
          className={`flex-1 py-2 px-4 rounded-lg font-medium transition-colors ${
            formData.type === 'expense'
              ? 'bg-red-500 text-white'
              : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
          }`}
        >
          Ausgabe
        </button>
      </div>
      <button
        onClick={handleSubmit}
        className="w-full py-2 px-4 bg-gradient-to-r from-indigo-600 to-purple-600 text-white rounded-lg font-medium hover:from-indigo-700 hover:to-purple-700 transition-all"
      >
        Hinzufügen
      </button>
    </div>
  );
}

function SavingsGoalForm({ onAdd }) {
  const [formData, setFormData] = useState({ name: '', targetAmount: '' });

  const handleSubmit = () => {
    if (formData.name && formData.targetAmount) {
      onAdd({
        name: formData.name,
        targetAmount: parseFloat(formData.targetAmount)
      });
      setFormData({ name: '', targetAmount: '' });
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSubmit();
    }
  };

  return (
    <div className="space-y-3">
      <input
        type="text"
        placeholder="Zielname"
        value={formData.name}
        onChange={(e) => setFormData({ ...formData, name: e.target.value })}
        onKeyPress={handleKeyPress}
        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
      />
      <input
        type="number"
        step="0.01"
        placeholder="Zielbetrag"
        value={formData.targetAmount}
        onChange={(e) => setFormData({ ...formData, targetAmount: e.target.value })}
        onKeyPress={handleKeyPress}
        className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
      />
      <button
        onClick={handleSubmit}
        className="w-full py-2 px-4 bg-indigo-600 text-white rounded-lg font-medium hover:bg-indigo-700 transition-colors"
      >
        Sparziel erstellen
      </button>
    </div>
  );
}

function SavingsGoalCard({ goal, onUpdate, onDelete }) {
  const [isEditing, setIsEditing] = useState(false);
  const [amount, setAmount] = useState('');
  const progress = (goal.currentAmount / goal.targetAmount) * 100;

  const handleUpdate = () => {
    if (amount) {
      onUpdate(goal._id, parseFloat(amount));
      setAmount('');
      setIsEditing(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleUpdate();
    }
  };

  return (
    <div className="bg-gradient-to-br from-indigo-50 to-purple-50 rounded-lg p-4 border border-indigo-200">
      <div className="flex items-start justify-between mb-3">
        <div className="flex items-center gap-2">
          <Target className="w-5 h-5 text-indigo-600" />
          <h3 className="font-semibold text-gray-900">{goal.name}</h3>
        </div>
        <button
          onClick={() => onDelete(goal._id)}
          className="p-1 hover:bg-red-100 rounded text-red-600 transition-colors"
        >
          <Trash2 className="w-4 h-4" />
        </button>
      </div>

      <div className="space-y-2">
        <div className="flex justify-between text-sm">
          <span className="text-gray-600">CHF {goal.currentAmount.toFixed(2)}</span>
          <span className="text-gray-600">CHF {goal.targetAmount.toFixed(2)}</span>
        </div>
        
        <div className="w-full bg-gray-200 rounded-full h-2">
          <div
            className="bg-gradient-to-r from-indigo-500 to-purple-600 h-2 rounded-full transition-all duration-500"
            style={{ width: `${Math.min(progress, 100)}%` }}
          />
        </div>
        
        <div className="text-center text-sm font-medium text-indigo-600">
          {progress.toFixed(1)}%
        </div>
      </div>

      {isEditing ? (
        <div className="flex gap-2 mt-3">
          <input
            type="number"
            step="0.01"
            placeholder="Betrag"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            onKeyPress={handleKeyPress}
            className="flex-1 px-3 py-1 border border-gray-300 rounded text-sm focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
          />
          <button
            onClick={handleUpdate}
            className="p-1 bg-green-500 text-white rounded hover:bg-green-600"
          >
            <Check className="w-4 h-4" />
          </button>
          <button
            onClick={() => setIsEditing(false)}
            className="p-1 bg-gray-300 text-gray-700 rounded hover:bg-gray-400"
          >
            <X className="w-4 h-4" />
          </button>
        </div>
      ) : (
        <button
          onClick={() => setIsEditing(true)}
          className="w-full mt-3 py-1 text-sm bg-white border border-indigo-300 text-indigo-600 rounded hover:bg-indigo-50 transition-colors"
        >
          Betrag hinzufügen
        </button>
      )}
    </div>
  );
}

export default Dashboard;
