import React, { useState } from 'react';
import './SavingsGoalCard.css';
import TransactionForm from './TransactionForm';

function SavingsGoalCard({ goal, onUpdate, onDelete }) {
  const [addAmount, setAddAmount] = useState('');
  
  const progress = (goal.currentAmount / goal.targetAmount) * 100;
  const remaining = goal.targetAmount - goal.currentAmount;
  
  const monthsRemaining = goal.deadline 
    ? Math.ceil((new Date(goal.deadline) - new Date()) / (1000 * 60 * 60 * 24 * 30))
    : null;

  const handleAddAmount = () => {
    if (addAmount) {
      const newAmount = goal.currentAmount + parseFloat(addAmount);
      onUpdate(goal._id, newAmount);
      setAddAmount('');
    }
  };

  const getCategoryIcon = (category) => {
    switch(category) {
      case 'Haus': return 'üè†';
      case 'Boot': return '‚õµ';
      case 'Auto': return 'üöó';
      default: return 'üíé';
    }
  };

  return (
    <div className="savings-goal-card">
      <div className="card-header">
        <h3>{getCategoryIcon(goal.category)} {goal.name}</h3>
        <button className="delete-btn" onClick={() => onDelete(goal._id)}>‚ùå</button>
      </div>

      <div className="progress-section">
        <div className="progress-bar">
          <div 
            className="progress-fill" 
            style={{ width: `${Math.min(progress, 100)}%` }}
          ></div>
        </div>
        <p className="progress-text">{progress.toFixed(1)}% erreicht</p>
      </div>

      <div className="amounts">
        <div className="amount-item">
          <span className="label">Gespart:</span>
          <span className="value">CHF {goal.currentAmount.toFixed(2)}</span>
        </div>
        <div className="amount-item">
          <span className="label">Ziel:</span>
          <span className="value">CHF {goal.targetAmount.toFixed(2)}</span>
        </div>
        <div className="amount-item remaining">
          <span className="label">Noch ben√∂tigt:</span>
          <span className="value">CHF {remaining.toFixed(2)}</span>
        </div>
      </div>

      {goal.monthlyContribution > 0 && (
        <p className="monthly-info">
          üìÖ Monatlich: CHF {goal.monthlyContribution.toFixed(2)}
        </p>
      )}

      {monthsRemaining && monthsRemaining > 0 && (
        <p className="deadline-info">
          ‚è∞ Noch {monthsRemaining} Monate bis {new Date(goal.deadline).toLocaleDateString('de-CH')}
        </p>
      )}

      <div className="add-amount-section">
        <input
          type="number"
          step="0.01"
          placeholder="Betrag hinzuf√ºgen"
          value={addAmount}
          onChange={(e) => setAddAmount(e.target.value)}
        />
        <button onClick={handleAddAmount}>üí∞ Einzahlen</button>
      </div>
    </div>
  );
}

export default TransactionForm;
