import React, { useState } from 'react';

function TransactionForm({ onAdd, savingsGoals }) {
  const [text, setText] = useState('');
  const [amount, setAmount] = useState('');
  const [type, setType] = useState('expense');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!text || !amount) return;

    onAdd({
      text,
      amount: parseFloat(amount),
      type
    });

    // Felder nach dem Absenden leeren
    setText('');
    setAmount('');
  };

  return (
    <form className="transaction-form" onSubmit={handleSubmit}>
      {/* Feld 1: Beschreibung */}
      <div className="input-group">
        <label htmlFor="text">Beschreibung / Sparziel</label>
        <input
          id="text"
          type="text"
          list="goal-suggestions"
          placeholder="z.B. Lohn oder Auto"
          value={text}
          onChange={(e) => setText(e.target.value)}
          required
        />
        {/* Vorschlagsliste aus den Sparzielen (Feature #10 Verbindung) */}
        <datalist id="goal-suggestions">
          {savingsGoals && savingsGoals.map(goal => (
            <option key={goal._id} value={goal.name} />
          ))}
        </datalist>
      </div>

      {/* Feld 2: Betrag */}
      <div className="input-group">
        <label htmlFor="amount">Betrag (CHF)</label>
        <input
          id="amount"
          type="number"
          step="0.01"
          placeholder="0.00"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          required
        />
      </div>

      {/* Feld 3: Typ */}
      <div className="input-group">
        <label htmlFor="type">Typ</label>
        <select 
          id="type" 
          value={type} 
          onChange={(e) => setType(e.target.value)}
        >
          <option value="expense">Ausgabe / Sparen</option>
          <option value="income">Einnahme</option>
        </select>
      </div>

      <button type="submit" className="submit-btn">Buchen</button>
    </form>
  );
}

export default TransactionForm;
